package controlstructures;

import java.util.Scanner;

public class Patttern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s=new Scanner(System.in);
		int n=s.nextInt();
		int c=s.nextInt();
		int b=s.nextInt();
		int z=0;
		for(int i=0;i<=n;i++) {
			z=((c+b)*n)/2;
			
		}
		System.out.println(z);
	}

}
